      </dynamic-element>
    </root>

    val journalArticle2 = addJournalArticleWithContent(defaultUserId, group.getGroupId,
      "Discussion", pictureContent.toString(), pictureBoxStructure.getStructureId, pictureBoxTemplate.getTemplateId,
